import React from 'react';
import Game from './Game';
import { connect } from 'react-redux';

class GameWrapper extends React.Component {
  onclick(action) {
    this.props.dispatch({
      type: action,
    })
  }

  render() {
      let renderedHTML = <div className="deck"> 
                              <button onClick={()=>this.onclick("EASY")}> Easy </button>
                              <button onClick={()=>this.onclick("MEDIUM")}> Medium </button>
                              <button onClick={()=>this.onclick("HARD")}> Hard </button> 
                          </div>
      if (this.props.gameMode !== undefined) {
          renderedHTML = <Game mode={this.props.gameMode}/>
      }
      return (
          <div>
              {renderedHTML}
          </div> 
      )
  }

}

let mapDispatchToProps = function(dispatch, props) {
    return {
        dispatch: dispatch,
    }
  }

  let mapStateToProps = function(state, props) {
    return {
      gameMode: state.game.gameMode,
    }
  }

  export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(GameWrapper)
