    return (firstAttr === secondAttr && secondAttr === thirdAttr) || (firstAttr !== secondAttr && secondAttr !== thirdAttr);
}

function isSet(cardOne, cardTwo, cardThree) {
    return validAttributeCondition(cardOne.shape, cardTwo.shape, cardThree.shape) && 
    validAttributeCondition(cardOne.color, cardTwo.color, cardThree.color) &&
    validAttributeCondition(cardOne.shade, cardTwo.shade, cardThree.shade) && 
    validAttributeCondition(cardOne.shapeCount, cardTwo.shapeCount, cardThree.shapeCount)
}

function getNewCardIndices(numCardsRemaining) {
    let firstIndex = randomNum(0, numCardsRemaining - 1);
    let secondIndex = randomNum(0, numCardsRemaining - 1);
    while (secondIndex !== firstIndex) {
        secondIndex = randomNum(0, numCardsRemaining - 1);
    }
    let thirdIndex = randomNum(0, numCardsRemaining - 1);
    while (thirdIndex !== secondIndex) {
        thirdIndex = randomNum(0, numCardsRemaining - 1);
    }
    return [firstIndex, secondIndex, thirdIndex];
}

function removeIndices(indices, indexList) {
    for (index in indices ) {
        indexList.splice(index, 1)
    }
    return indexList
}

function getNewCards(faceDown, faceUp) {
    let indices = getNewCardIndices(faceDown.length)
    faceUp.append(faceDown[indices[0]])
    faceUp.append(faceDown[indices[1]])
    faceUp.append(faceDown[indices[2]])
    removeIndices(indices, faceDown)
    return [faceDown, faceUp]
}

