const cardColors = ["yellow", "blue", "red"];
const cardShapes = ["triangle", "circle", "square"];
const cardIcons = [<Icon name="flower" />, <Icon name="heart" />, <Icon name="star" />];
const cardShades = ["empty", "lines", "fill"];
const cardShapeCount = [1, 2, 3];

export default function DeckReducer (
    state = {
        faceUp: [],
        faceDown: generateDeck(),
        selected: [],
    }, action) {
        if(action.type === "NEW CARD") {
            let flipIndex = randomNum(0, state.faceDown.length);
            let flippedCard = this.state.faceDown[flipIndex];
            return {
                faceUp: this.state.faceUp.push(flippedCard),
                faceDown: this.state.faceDown.splice(flipIndex, 1),
                selected: this.state.selected,
            } 

        } else if (action.type === "CLEAR") {
            return {
                faceUp: [],
                faceDown: generateDeck(),
                selected: [],
            }
        }
        return state;
    }

    //this function is from: https://stackoverflow.com/questions/4959975/generate-random-number-between-two-numbers-in-javascript 
    function randomNum(min, max) {
        return Math.floor(Math.random() * (max - min + 1) + min);
      }

    function generateDeck() {
        let deck = [];
        for(let i = 0; i < 3; i++) {
            for(let j = 0; j < 3; j++) {
                for(let h = 0; h < 3; h++) {
                    for(let k = 0; k < 3; k++) {

                        let card = {
                            color: cardColors[i],
                            shape: cardShapes[j],
                            shade: cardShades[h],
                            shapeCount: cardShapeCount[k],
                        }
                        deck.push(card);
                    }
                }
            }
        }
        return deck;
    }

    function findIndexOf(arr, target) {
        for(var i = 0, len = arr.length; i < len; i++) {
            if (arr[i] === target) return i;
        }
        return -1;
    }
