import React from 'react';
import ReactDOM from 'react-dom';
import { createStore } from 'redux';
import { Provider } from 'react-redux';
import App from './App.js';
import DeckReducer from './reducers/DeckReducer.js';

const cardColors = ["yellow", "blue", "red"];
const cardShapes = ["triangle", "circle", "square"];
//const cardIcons = [<Icon name="flower" />, <Icon name="heart" />, <Icon name="star" />]
const cardShades = ["empty", "lines", "fill"];
const cardShapeCount = [1, 2, 3];

let defaultState = {
        faceUp: [],
        faceDown: generateDeck(),
        selected: [],
}

const store = createStore(DeckReducer, defaultState);

ReactDOM.render(
  <Provider store={store}>
    <App />
  </Provider>, document.getElementById('root'));

function generateDeck() {
  let deck = [];
  for(let i = 0; i < 3; i++) {
      for(let j = 0; j < 3; j++) {
          for(let h = 0; h < 3; h++) {
              for(let k = 0; k < 3; k++) {

                  let card = {
                      color: cardColors[i],
                      shape: cardShapes[j],
                      shade: cardShades[h],
                      shapeCount: cardShapeCount[k],
                  }
                  deck.push(card);
              }
          }
      }
  }
  return deck;
}
