import Icon from "./Icon";
import React from 'react';
import '../style/Card.css';

export default class Card extends React.Component {

    render() {

        let icons = [];
        let cardIcon = <Icon type={this.props.attributes.shape} color={this.props.attributes.color} fill={this.props.attributes.shade}></Icon>;


        for(let i = 0; i < this.props.attributes.shapeCount; i++) {
            icons.push(cardIcon);
        };


        return (
            <div className="card">
                <div id="icon-container">
                    {icons}
                </div>
            </div>
        )
    }

}
